mapper.select:
select * from renwu where zx_id = #{zx_id}
and type like #{type}
order by start_time, type

select * from renwu where u_id = #{u_id}
and type like #{type}
order by start_time, type

mapper.insert:
insert into renwu(id, name, ms, u_id, u_name, zx_id, zx_name, start_time, end_time, expected_time, type, yxj,
                  qy_id, bm_id) value (
                                       #{id}, #{name}, #{ms}, #{u_id}, #{u_name}, #{zx_id}, #{zx_name},
                                       #{start_time}, #{end_time}, #{expected_time}, #{type}, #{yxj}, #{qy_id},
                                       #{bm_id}
    )

mapper.update:
update renwu
set type=#{type},
    end_time=#{end_time}
where id = #{id}
